import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'


const ContenedorNavBar = styled.nav`
    display: flex;
    flex-direction: row;
    align-items: baseline;
    position: relative;
    background-color: #464646;
    //justify-content: space-between;
    color: white;
`

const ContenedorP = styled.div`
    display: flex;
    flex-direction: row;
    position: relative;
    justify-content: center;
    padding-top: 1rem;
    padding-left: 1rem;
    font-size: larger;
`
const NavbarTitle = styled(Link)`
color:white;
font-size: larger;
font-family: Arial, Helvetica, sans-serif;
text-decoration: none;
margin: 0px;
padding-left: 2rem;
&:hover,
&:focus{
    color: white;
}
&:active{
    color: black;
};
`

const NavbarAbout = styled(Link)`
color:white;
font-size: Medium;
font-family: Arial, Helvetica, sans-serif;
text-decoration: none;
&:hover,
&:focus{
    color: white;
}
&:active{
    color: black;
};
`


function FooterBar({onSearch}) {
    return <ContenedorNavBar>
        <ContenedorP>
            <NavbarTitle to= "/" >
                <p>Weather App</p>
            </NavbarTitle>
        </ContenedorP>
        <NavbarAbout to='/about'>
            <a href="https://www.linkedin.com/in/juancortez-dev/">LinkedIn</a>
            <a href="https://github.com/jicortez">Github</a>        
        </NavbarAbout>
    </ContenedorNavBar>
    
}


export default FooterBar;
