import React, { useState } from 'react';
import NavBar from './components/Nav.jsx';
import Cards from "./components/Cards.jsx"
import './App.css';
import { Route } from 'react-router-dom';
import styled from 'styled-components';

const ContenedorCards = styled.div`
  text-align: center;
  background: linear-gradient(to bottom, #0b486b, #f56217); /* W3C, IE 10+/ Edge, Firefox 16+, Chrome 26+, Opera 12+, Safari 7+ */
  height: 100vh;
  width: 100%;
`


function App() {
  const [cities, setCities] = useState([]);

  const apiKey ="4ae2636d8dfbdc3044bede63951a019b";

  function onSearch(city) {
    fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`)
    .then(response => response.json())
    .then(response_json => {
      if(response_json.main !== undefined){
        const city = {
          min: Math.round(response_json.main.temp_min),
          max: Math.round(response_json.main.temp_max),
          id:response_json.id,
          img: response_json.weather[0].icon,
          wind: response_json.wind.speed,
          temp: response_json.main.temp,
          name: response_json.name,
          longitude: response_json.coord.lon,
          latitude: response_json.coord.lat
        }
        setCities(oldCities => [...oldCities, city]);
      } else {
        alert("Ciudad no encontrada!");
      }
    })
    .catch(e => console.log(e));
  }

  function onClose(id){
    setCities(oldCities => oldCities.filter(c => c.id !== id));
  }

  return (
    <div className="App">
      <Route
    path='/'
    render={() => <Nav onSearch={onSearch} />}
/>
<Route
    path='/about'
    component={About}
/>
      <NavBar onSearch={onSearch}/>
      <ContenedorCards>
        <Cards cities ={cities} onClose={onClose}/>
      </ContenedorCards>
    </div>
  );
}

export default App;
